	/**
	 * Gets an array of BoardCommittee objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this BoardMember has previously been saved, it will retrieve
	 * related BoardCommittees from storage. If this BoardMember is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array BoardCommittee[]
	 * @throws     PropelException
	 */
	public function getBoardCommittees($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(BoardMemberPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collBoardCommittees === null) {
			if ($this->isNew()) {
			   $this->collBoardCommittees = array();
			} else {

				$criteria->add(BoardCommitteePeer::BOARD_MEMBER_ID, $this->id);

				BoardCommitteePeer::addSelectColumns($criteria);
				$this->collBoardCommittees = BoardCommitteePeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(BoardCommitteePeer::BOARD_MEMBER_ID, $this->id);

				BoardCommitteePeer::addSelectColumns($criteria);
				if (!isset($this->lastBoardCommitteeCriteria) || !$this->lastBoardCommitteeCriteria->equals($criteria)) {
					$this->collBoardCommittees = BoardCommitteePeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastBoardCommitteeCriteria = $criteria;
		return $this->collBoardCommittees;
	}

	/**
	 * Returns the number of related BoardCommittee objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related BoardCommittee objects.
	 * @throws     PropelException
	 */
	public function countBoardCommittees(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(BoardMemberPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collBoardCommittees === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(BoardCommitteePeer::BOARD_MEMBER_ID, $this->id);

				$count = BoardCommitteePeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(BoardCommitteePeer::BOARD_MEMBER_ID, $this->id);

				if (!isset($this->lastBoardCommitteeCriteria) || !$this->lastBoardCommitteeCriteria->equals($criteria)) {
					$count = BoardCommitteePeer::doCount($criteria, $con);
				} else {
					$count = count($this->collBoardCommittees);
				}
			} else {
				$count = count($this->collBoardCommittees);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a BoardCommittee object to this object
	 * through the BoardCommittee foreign key attribute.
	 *
	 * @param      BoardCommittee $l BoardCommittee
	 * @return     void
	 * @throws     PropelException
	 */
	public function addBoardCommittee(BoardCommittee $l)
	{
		if ($this->collBoardCommittees === null) {
			$this->initBoardCommittees();
		}
		if (!in_array($l, $this->collBoardCommittees, true)) { // only add it if the **same** object is not already associated
			array_push($this->collBoardCommittees, $l);
			$l->setBoardMember($this);
		}
	}


