				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				DonationPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded

			$key2 = DonorPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
