					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					DonorPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded

				// Add the $obj1 (Donation) to $obj2 (Donor)
				$obj2->addDonation($obj1);

