	 * Selects a collection of Donor objects pre-filled with all related objects except Company.
	 *
	 * @param      Criteria  $c
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of Donor objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptCompany(Criteria $c, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		// $c->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		DonorPeer::addSelectColumns($c);
		$startcol2 = (DonorPeer::NUM_COLUMNS - DonorPeer::NUM_LAZY_LOAD_COLUMNS);

		AffiliationPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (AffiliationPeer::NUM_COLUMNS - AffiliationPeer::NUM_LAZY_LOAD_COLUMNS);

		PersonPeer::addSelectColumns($c);
		$startcol4 = $startcol3 + (PersonPeer::NUM_COLUMNS - PersonPeer::NUM_LAZY_LOAD_COLUMNS);

				$c->addJoin(array(DonorPeer::AFFILIATION_ID,), array(AffiliationPeer::ID,), $join_behavior);
				$c->addJoin(array(DonorPeer::PERSON_ID,), array(PersonPeer::ID,), $join_behavior);

		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = DonorPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = DonorPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = DonorPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				DonorPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Affiliation rows

				$key2 = AffiliationPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = AffiliationPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$omClass = AffiliationPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					AffiliationPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Donor) to the collection in $obj2 (Affiliation)
				$obj2->addDonor($obj1);

			} // if joined row is not null

				// Add objects for joined Person rows

				$key3 = PersonPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = PersonPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$omClass = PersonPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					PersonPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Donor) to the collection in $obj3 (Person)
				$obj3->addDonor($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


