    {
      $ret = call_user_func($callable, 'BasePassengerBackupPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(PassengerBackupPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from PassengerBackup object
		}

		if ($criteria->containsKey(PassengerBackupPeer::ID) && $criteria->keyContainsValue(PassengerBackupPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.PassengerBackupPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
