	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(PassengerBackupPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(PassengerBackupPeer::DATABASE_NAME);
			$criteria->add(PassengerBackupPeer::ID, $pks, Criteria::IN);
			$objs = PassengerBackupPeer::doSelect($criteria, $con);
