	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseRpApplicationSearchPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseRpApplicationSearchPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(RpApplicationSearchPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from RpApplicationSearch object
		}

		if ($criteria->containsKey(RpApplicationSearchPeer::ID) && $criteria->keyContainsValue(RpApplicationSearchPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.RpApplicationSearchPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BaseRpApplicationSearchPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BaseRpApplicationSearchPeer', $values, $con, $pk);
    }

    return $pk;
	}

